import React, { useEffect, useRef, useState } from 'react';
import { logo, language } from './assets';
import { BATTERY_TYPES } from "./constants"

import './App.css';

function App() {
  const [transformerCount, setTransformerCount] = useState(0);
  const [totalCost, setTotalCost] = useState(0);
  const [totalEnergy, setTotalEnergy] = useState(0);
  const [totalSize, setTotalSize] = useState(0);
  const [boxes, setBoxes] = useState([]);
  const [counts, setCounts] = useState({
    mpxl: 0,
    mp2: 0,
    mp: 0,
    pp: 0,
  });

  const containerRef = useRef(null);
  const draggedBoxId = useRef(null);
  const isDragging = useRef(false);
  const dragStartCoords = useRef({
    startX: 0,
    startY: 0,
    startLeft: 0,
    startTop: 0,
  });

  useEffect(() => {
    if (!containerRef.current) return;

    const container = containerRef.current;

    const onMouseUp = () => {
      isDragging.current = false;
      draggedBoxId.current = null;
    };

    const onMouseMove = (e) => {
      if (!isDragging.current || !draggedBoxId.current) return;

      const boxId = draggedBoxId.current;
      const box = document.getElementById(boxId);
      if (!box) return;

      const nextX = e.clientX - dragStartCoords.current.startX + dragStartCoords.current.startLeft;
      const nextY = e.clientY - dragStartCoords.current.startY + dragStartCoords.current.startTop;

      box.style.left = `${nextX}px`;
      box.style.top = `${nextY}px`;
    };

    container.addEventListener('mousemove', onMouseMove);
    container.addEventListener('mouseup', onMouseUp);
    container.addEventListener('mouseleave', onMouseUp);

    return () => {
      container.removeEventListener('mousemove', onMouseMove);
      container.removeEventListener('mouseup', onMouseUp);
      container.removeEventListener('mouseleave', onMouseUp);
    };
  }, []);

  useEffect(() => {
    if (draggedBoxId.current) {
      const boxId = draggedBoxId.current;
      const updatedBoxes = boxes.map((box) => {
        if (box.id === boxId) {
          const boxElement = document.getElementById(boxId);
          return {
            ...box,
            x: boxElement?.offsetLeft ?? box.x,
            y: boxElement?.offsetTop ?? box.y,
          };
        }
        return box;
      });
      setBoxes(updatedBoxes);
    }
  }, [boxes]);

  useEffect(() => {
    getTotals();
    getBoxes(counts);
  }, [counts]);

  const getBoxes = (counts) => {
    const items = [];
    const countItems = Object.entries(counts);
    let prevSize = 0;
    let idx = 0;

    countItems.forEach(([battery, count]) => {
      while (count > 0) {
        const item = {
          type: battery,
          id: `${battery}_${idx}`,
          x: idx % 2 === 0 ? 0 : idx % 2 * prevSize,
          y: Math.floor(idx / 2) * 100,
        };

        items.push(item);

        count--;
        idx++;
        prevSize = BATTERY_TYPES[battery].size;
      }
    });

    setBoxes(items);
  };

  const getTransformerCount = (updatedCounts) => {
    const total = Object.values(updatedCounts).reduce((acc, curr) => acc + curr, 0);
    return Math.floor(total / 2);
  };

  const handleQuantityChange = (e, id) => {
    const newCounts = {
      ...counts,
      [id]: parseInt(e.target.value, 10),
    };

    setCounts(newCounts);
    setTransformerCount(getTransformerCount(newCounts));
  };

  const getTotals = () => {
    const cost = BATTERY_TYPES.mpxl.cost * counts.mpxl + BATTERY_TYPES.mp2.cost * counts.mp2 + BATTERY_TYPES.mp.cost * counts.mp + BATTERY_TYPES.pp.cost * counts.pp + BATTERY_TYPES.t.cost * transformerCount;
    const energy = BATTERY_TYPES.mpxl.energy * counts.mpxl + BATTERY_TYPES.mp2.energy * counts.mp2 + BATTERY_TYPES.mp.energy * counts.mp + BATTERY_TYPES.pp.energy * counts.pp + BATTERY_TYPES.t.energy * transformerCount;
    const size = BATTERY_TYPES.mpxl.size * counts.mpxl + BATTERY_TYPES.mp2.size * counts.mp2 + BATTERY_TYPES.mp.size * counts.mp + BATTERY_TYPES.pp.size * counts.pp + BATTERY_TYPES.t.size * transformerCount;

    setTotalCost(cost);
    setTotalEnergy(energy);
    setTotalSize(size);
  };

  const onMouseDown = (e, boxId) => {
    const box = document.getElementById(boxId);
    if (!box) return;

    isDragging.current = true;
    draggedBoxId.current = boxId;
    dragStartCoords.current.startX = e.clientX;
    dragStartCoords.current.startY = e.clientY;
    dragStartCoords.current.startLeft = box.offsetLeft;
    dragStartCoords.current.startTop = box.offsetTop;
  };

  return (
    <>
      <div className="header">
        <img src={logo} className="logo" alt="Logo" />
        <img src={language} className="language" alt="Language" />
      </div>

      <div className="main">
        <div className="dashboard">
          <div className="total">
            <div className="title">Battery Configurator</div>
            <div className="grid-container">
              <div className="grid-item">
                <p>Total Cost</p>
                <p>{totalCost}</p>
              </div>
              <div className="grid-item">
                <p>Total Energy</p>
                <p>{totalEnergy}</p>
              </div>
              <div className="grid-item">
                <p>Total Size</p>
                <p>{totalSize}</p>
              </div>
            </div>
          </div>
          <div className="quantity">
            <table className="table">
              <thead>
                <tr>
                  <th>Battery Type</th>
                  <th>Cost/unit ($)</th>
                  <th>Energy/unit (MWh)</th>
                  <th>Quantity</th>
                </tr>
              </thead>
              <tbody>
                {Object.values(BATTERY_TYPES).map((item) => (
                  <tr key={item.id}>
                    <td>{item.name}</td>
                    <td>{item.cost.toLocaleString()}</td>
                    <td>{item.energy}</td>
                    <td>
                      <div>
                        <input
                          aria-label="quantity count"
                          disabled={item.id === "t"}
                          max="1000"
                          min="0"
                          type="number"
                          value={item.id === "t" ? transformerCount : counts[item.id]}
                          onChange={(e) => handleQuantityChange(e, item.id)}
                        />
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <span className="caption">1 transformer is added for every 2 batteries purchased</span>
          </div>
        </div>
        <div ref={containerRef} className="container">
          {boxes.map((box) => (
            <div
              key={box.id}
              id={box.id}
              className={`box ${box.type}`}
              style={{ left: `${box.x}px`, top: `${box.y}px` }}
              onMouseDown={(e) => onMouseDown(e, box.id)}
            >
              {box.id}
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default App;
