// import React, { useState } from 'react';

// import { QuantitySelector } from "./components/QuantitySelector"

import { megapack, megapack_2, megapack_xl, powerpack, logo, language } from './assets'; 

// import './App.css';

const data = [{
  id: "mpxl",
  name: "MegapackXL",
  cost: "$120,000",
  energy: "4 MWh",
  totalCost:"$1567,000",
  totalEnergy: "8MWH",
}, {
  id: "mp2",
  name: "Megapack2",
  cost: "$80,000",
  energy: "3 MWh",
  totalCost:"$1567,000",
  totalEnergy: "8MWH",
},
{
  id: "mp",
  name: "Megapack",
  cost: "$50,000",
  energy: "2 MWh",
  totalCost:"$1567,000",
  totalEnergy: "8MWH",
},
{
  id: "pp",
  name: "Powerpack",
  cost: "$10,000",
  energy: "1 MWh",
  totalCost:"$1567,000",
  totalEnergy: "8MWH",
},
// {
//   id: "t",
//   name: "Transformer",
//   cost: "$10,000",
//   energy: "-0.5 MWh",
//   totalCost:"$1567,000",
//   totalEnergy: "8MWH",
// },
]


const data2 = {
  mpxl: {
  id: "mpxl",
  name: "MegapackXL",
  cost: 120000,
  energy: 4,
  size: 400,
  totalCost:1567000,
  totalEnergy: "8MWH",
}, 
mp2: {
  id: "mp2",
  name: "Megapack2",
  cost: 80000,
  energy: 3,
  size: 300,
  totalCost:1567000,
  totalEnergy: "8MWH",
},
mp: {
  id: "mp",
  name: "Megapack",
  cost: 50000,
  energy: 2,
  size: 300,
  totalCost:1567000,
  totalEnergy: "8MWH",
},
pp: {
  id: "pp",
  name: "Powerpack",
  cost: 10000,
  energy: 1,
  size: 100,
  totalCost:1567000,
  totalEnergy: "8MWH",
},
t: {
  id: "t",
  name: "Transformer",
  cost: 10000,
  energy: -0.5,
  size: 100,
  totalCost:1567000,
  totalEnergy: "8MWH",
}
}

// export default App;

import React, { useEffect, useRef, useState } from 'react';
import './App.css';

function App() {

  const [transformerCount, setTransformerCount] = useState(0);
  const [totalCost, setTotalCost] = useState(0)
  const [totalEnergy, setTotalEnergy] = useState(0)
  const [totalSize, setTotalSize] = useState(0)
  const [totalCount, setTotalCount] = useState(0)

  // Initial state of boxes as an array
  const [boxes, setBoxes] = useState([]);

  const getBoxes = (counts) => {
    console.log("counts", counts)
    const items = []
    const countItems = Object.entries(counts)
    let prevSize = 0
    let idx = 0

    countItems.forEach(([battery, count]) => {
      prevSize = data2[battery].size 
      // console.log(battery, count, prevSize, idx % 2)


      while(idx < count) {
        const item = {
          id: `${battery}_${idx}`,
          x: idx % 2 === 0 ? 0 : idx % 2 * prevSize,
          y: Math.floor(idx/ 2) * 100
        }

        items.push(item)

        idx++
      }

    })

    console.log(items)

    setBoxes(items)
  }

  const containerRef = useRef(null);
  const draggedBoxId = useRef(null);
  const isDragging = useRef(false);
  const dragStartCoords = useRef({
    startX: 0,
    startY: 0,
    startLeft: 0,
    startTop: 0
  });

  const [counts, setCounts] = useState({
    mpxl: 0,
    mp2: 0,
    mp: 0,
    pp: 0,
    // t: 0,
  });




  const onMouseDown = (e, boxId) => {
    const box = document.getElementById(boxId);
    if (!box) return;

    isDragging.current = true;
    draggedBoxId.current = boxId;
    dragStartCoords.current.startX = e.clientX;
    dragStartCoords.current.startY = e.clientY;
    dragStartCoords.current.startLeft = box.offsetLeft;
    dragStartCoords.current.startTop = box.offsetTop;
  }



  useEffect(() => {
    if (!containerRef.current) return;

    const container = containerRef.current;

    const onMouseUp = () => {
      isDragging.current = false;
      draggedBoxId.current = null;
    }

    const onMouseMove = (e) => {
      if (!isDragging.current || !draggedBoxId.current) return;

      const boxId = draggedBoxId.current;
      const box = document.getElementById(boxId);
      if (!box) return;

      const nextX = e.clientX - dragStartCoords.current.startX + dragStartCoords.current.startLeft;
      const nextY = e.clientY - dragStartCoords.current.startY + dragStartCoords.current.startTop;

      box.style.left = `${nextX}px`;
      box.style.top = `${nextY}px`;
    }

    container.addEventListener('mousemove', onMouseMove);
    container.addEventListener('mouseup', onMouseUp);
    container.addEventListener('mouseleave', onMouseUp);

    return () => {
      container.removeEventListener('mousemove', onMouseMove);
      container.removeEventListener('mouseup', onMouseUp);
      container.removeEventListener('mouseleave', onMouseUp);
    };
  }, []);

  useEffect(() => {
    // Update box positions in state when positions are changed
    if (draggedBoxId.current) {
      const boxId = draggedBoxId.current;
      const updatedBoxes = boxes.map(box => {
        if (box.id === boxId) {
          const boxElement = document.getElementById(boxId);
          return {
            ...box,
            x: boxElement?.offsetLeft ?? box.x,
            y: boxElement?.offsetTop ?? box.y,
          };
        }
        return box;
      });
      setBoxes(updatedBoxes);
    }
  }, [boxes]);


  useEffect(() => {
    getTotals();
    getBoxes(counts)
  }, [counts]);

  const getTransformerCount = (updatedCounts) => {
    const total = Object.values(updatedCounts).reduce((acc, curr) => acc + curr, 0);
    return Math.floor(total / 2);
  };



  const handleQuantityChange = (e, id) => {
    const newCounts = {
      ...counts,
      [id]: parseInt(e.target.value),
    };

    setCounts(newCounts);

    const transformerCount = getTransformerCount(newCounts);

    setTransformerCount(transformerCount)
  };

  const getTotals = () => {
    const count = counts.mpxl + counts.mp2 + counts.mp + counts.pp
    const cost = data2.mpxl.cost * counts.mpxl + data2.mp2.cost * counts.mp2 + data2.mp.cost * counts.mp + data2.pp.cost * counts.pp + data2.t.cost * transformerCount
    const energy = data2.mpxl.energy * counts.mpxl + data2.mp2.energy * counts.mp2 + data2.mp.energy * counts.mp + data2.pp.energy * counts.pp + data2.t.energy * transformerCount
    const size = data2.mpxl.size * counts.mpxl + data2.mp2.size * counts.mp2 + data2.mp.size * counts.mp + data2.pp.size * counts.pp + data2.t.size * transformerCount

    setTotalCost(cost)
    setTotalEnergy(energy)
    setTotalSize(size)
    setTotalCount(count)
  }


  return (
    <>
      <div className="header">
        <img src={logo} className="logo"></img>
        <img src={language} className="language"></img>
      </div>

        <div className="main">
          <div className="dashboard">
            <div className="total">
              <h1>Select Batteries</h1>

              <div>
                <p>Total Cost</p>
                <p>{totalCost}</p>
              </div>
              <div>
                <p>Total Energy</p>
                <p>{totalEnergy}</p>
              </div>
              <div>
                <p>Total Size</p>
                <p>{totalSize}</p>
              </div>
            </div>
            <div className="quantity">
              <table className="table">
                <thead>
                  <tr>
                    <th>Battery Type</th>
                    <th>Cost/Unit</th>
                    <th>Energy/Unit</th>
                    <th>Quantity</th>
                    {/* <th>Total Cost</th>
                    <th>Total Energy</th> */}
                  </tr>
                </thead>
                <tbody>
                  {data.map((item, index) => (
                    <tr key={index}>
                      <td>{item.name}
                      {/* <img src={megapack_xl} alt="Description of the image" /> */}
                      </td>
                      <td>{item.cost}</td>
                      <td>{item.energy}</td>
                      <td>{<div className="quantity-selector">
                      <input aria-label="quantity count" max="1000" min="0" type="number" value={counts[item.id]} onChange={(e) => handleQuantityChange(e, item.id)}/>
                    </div>}</td>
                      {/* <td>{item.totalCost}</td>
                      <td>{item.totalEnergy}</td> */}
                    </tr>
                  ))}
                  <tr>
                      <td>"Transformer"</td>
                      <td>10000</td>
                      <td>-0.5</td>
                      <td>{<div className="quantity-selector">
                      <input disabled aria-label="quantity count" type="number" value={transformerCount}/>
                    </div>}</td>
                    </tr>
                </tbody>
              </table>
              <span>1 transformer is neded for every 2 batteries purchased</span>
            </div>
          </div>
          <div ref={containerRef} className="container">
            {boxes.map(box => (
              <div
                key={box.id}
                id={box.id}
                className="box"
                style={{ left: `${box.x}px`, top: `${box.y}px` }}
                onMouseDown={(e) => onMouseDown(e, box.id)}
              >
                {box.id}
              </div>
            ))}
          </div>
        </div>
    </>
  );
}

export default App;
